            and standardAwardId = #{standardAward.standardAwardId}


            and name like CONCAT(#{standardAward.name},'%')


            and category = #{standardAward.category}


            and level = #{standardAward.level}


            and score = #{standardAward.score}


            and isActive = #{standardAward.isActive}


            and createdBy = #{standardAward.createdBy}


            and updatedBy = #{standardAward.updatedBy}



order by standardAwardId desc
